where P: AsyncPredicate<Request>, S: Service<P::Request>
{ state: State<P::Future, S::Future>, service: S
} AsyncResponseFuture 的 poll 结果由当前 state 决定。 impl<P, S, Request> Future for AsyncResponseFuture<P, S, Request>
where P: AsyncPredicate<Request>, S: Service<P::Request>, S::Error: Into<crate::BoxError>,
{ type Output = Result<S::Response, crate::BoxError>;
​ fn poll(self: Pin<&mut Self>, cx: &mut Context<'_>) -> Poll<Self::Output> { let mut this = self.project();
